#if ENABLE_INPUT_SYSTEMusing UnityEngine.InputSystem;#endifusing System.Collections;using System.Collections.Generic;using UnityEngine;public class LookWithMouse : MonoBehaviour{    public Transform playerBody;    public float sensitivityX = 15F;    public float sensitivityY = 15F;    public float minimumX = -360F;    public float maximumX = 360F;    public float minimumY = -80F;    public float maximumY = 80F;    float rotationY = 0F;    void Start()    {        Cursor.lockState = CursorLockMode.Locked;    }    void Update()    {        float rotationX = playerBody.localEulerAngles.y + Input.GetAxis("Mouse X") * sensitivityX;        rotationY += Input.GetAxis("Mouse Y") * sensitivityY;        rotationY = Mathf.Clamp(rotationY, minimumY, maximumY);        transform.localEulerAngles = new Vector3(-rotationY, 0, 0);        playerBody.localEulerAngles = new Vector3(0, rotationX, 0);    }}